import React, { Component } from 'react'

export default class FlipCoin extends Component {
    state={
        topSide:true,
        bckSource:'data:image/jpeg;base64,',
        topSource :'data:image/jpeg;base64,'
        ,topsidecount:0,
        backsidecount:0
        }
    Flip=()=>{
        var result=Math.floor(Math.random()*2)
        console.log(result)
        var resulttext;
        if(result===1) resulttext=true
        else resulttext=false;
        this.setState({topSide:resulttext})
        if(this.state.topSide){
           this.setState({topsidecount:this.state.topsidecount+1})
        }
        else{
            this.setState({backsidecount:this.state.backsidecount+1})
        }
        
    }
    render() {
        return (
            <div>
                <div>
                    Start
                </div>
                <div>
                    <img src={!this.state.topSide ? this.state.bckSource : this.state.topSource} alt="ss"></img>
                </div>
                <div>
                    <button onClick={this.Flip}>Flip</button>
                </div>
                <div> Gold:{this.state.topsidecount}</div>
                <div> Silver:{this.state.backsidecount}</div>
            </div>
        )
    }
}
